<section class="py-12 border-b border-zinc-900/50">
  <div class="max-w-3xl mx-auto px-4 sm:px-6 border-x border-zinc-900/50 h-full">
    <h2 class="text-xl font-bold text-white mb-6">Stack</h2>
    
    <div class="flex flex-wrap gap-3">
      {#each stack as tech}
        <a href={tech.url} target="_blank" rel="noopener noreferrer" 
           class="group relative flex items-center justify-center w-12 h-12 bg-zinc-900/50 border border-zinc-800 rounded-xl hover:bg-zinc-800 hover:border-zinc-700 transition-all hover:scale-110">
          <span class="w-6 h-6 flex items-center justify-center grayscale group-hover:grayscale-0 transition-all duration-300">
            {@html tech.icon}
          </span>
          
          <div class="absolute -top-10 left-1/2 transform -translate-x-1/2 px-2 py-1 bg-zinc-800 text-zinc-300 text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-10 border border-zinc-700">
            {tech.name}
          </div>
        </a>
      {/each}
    </div>
  </div>
</section>
